var liitleQ = function () {
    this.ada = require('../adapter');
};

liitleQ.prototype.findQ = function (str,callback) {
    var sql = "select * from t_littleQ where qusetion like ? ";
    str = "%"+str+"%";
    this.ada.all(sql,[str],function (res,data) {
        if(res){
            callback(res,data);
        }
        else {
            callback(res);
        }
    })
};

module.exports = new liitleQ();