var TradeClassify = function () {
    this.ada = require('../adapter');
};

TradeClassify.prototype.getClassify = function (callback) {
    var sql = 'select * from t_tradeClassify';
    this.ada.all(sql,[],function (res,data) {
        if(res){
            callback(data)
        }
    })
};

module.exports = new TradeClassify();