var TradePhoto = function () {
    this.ada = require('../adapter');
};

TradePhoto.prototype.getPhoto = function (tradeId,callback) {
    var sql = 'select * from t_tradePhoto where tradeId = ? order by tradePhotoPath';
    this.ada.all(sql,[tradeId],function (res,data) {
        if(res){
                callback(data);
        }
    })
};


module.exports = new TradePhoto();