INSERT INTO t_littleQ (qusetion, answer) VALUES ('商品签收时，应注意什么？', '您收货时，请确保商品的质量、数量、包装、配件等是否相符。如发现外包装破损或商品不符，请在单据上注明原因并予以拒收，同时尽快联系客服。若您签收后才发现快递存在缺件、破损等情况，请在24小时内联系客服并予以举证，逾期不予处理。');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('买到的都是正品吗？', '为保障消费者权益，100%正品是本商城的基本承诺，供货环节层层把关，保证出售的都是原厂正品商品，品质有保障。');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('账户密码忘记如何找回？', '本商城暂不支持账户密码找回，请牢记自己的账户密码');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('收货地址写错了、想换个地址怎么办？', '点击网页右上角“用户中心”，进入“用户中心”页面，选“我的资料”，然后再选择“我的地址”，点击“编辑”可以对收货地址进行修改。点击右上角的加号可以添加新的收货地址，点击“设为默认地址”可以修改正在使用的收货地址。INSERT INTO t_littleQ (qusetion, answer) VALUES ('为什么后下的订单先收到了，之前下的订单还没有收到？是按什么顺序发货的？', '为了单位时间内以最快速度处理尽量多订单，我们会适当综合按订单里货品配货的情况安排发货顺序，因此可能会出现订单发货和下单顺序不完全一致的情况。我们会尽一切努力为您尽快发货。');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('下单后可以修改订单吗？ ', '在订单发货之前可以修改，请联系在线客服');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('取消后还能恢复吗？', '订单一旦取消，将无法恢复，请您慎重操作。');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('能否货到付款？', '商城暂不支持货到付款');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('可以开增值税专用发票吗？', '索要增值税专用发票的顾客请联系客服，按照增值税专票流程进行开具。');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('我想办理退货或换货如何操作？', '请联系在线客服来进行退换货');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('如何取消订单？', '在订单支付之前，您可以进入"个人中心-我的订单"自行操作取消订单');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('订单已提交成功，什么时候可以发货？', '订单支付成功后我们会尽快发货');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('我买的东西出现质量问题怎么办？', '如在质保期内，正常使用过程发现质量问题，请立刻联系商城售后客服，我们会竭诚为您服务，为您圆满的解决。');
INSERT INTO t_littleQ (qusetion, answer) VALUES ('网上支付时，如果没有我需要的银行怎么办？', '如果您在网上银行中没有找到需要的银行（比如邮政储蓄银行、平安银行、中信银行等），您可以选择支付宝或者财付通进行支付。');
